import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { lastValueFrom } from 'rxjs';
import {DataService} from '../services/data.service'

@Component({
  selector: 'app-problem',
  templateUrl: './problem.component.html',
  styleUrls: ['./problem.component.css']
})
export class ProblemComponent implements OnInit {
  problem!:any;
  write_solution:boolean = false;
  solution_code!:string;
  solution_output!:any;
  discussions!:any;
  Problem_Discussion:boolean= true; // true- displays problem | false- displays discussion
  constructor(private route:ActivatedRoute, public service:DataService, public http:HttpClient ) {

   }

  async ngOnInit(): Promise<void> {
    let id= this.route.snapshot.paramMap.get('id');
    this.problem = await this.service.displayProblem(id);
    this.discussions = await this.service.getDiscussionsOfProblem(id);
  }
  Display_Problem_Discussion(){
    this.Problem_Discussion= !this.Problem_Discussion;
  }

  async solutionSubmit(){
    let obj = {
      language: "python3",
      script:this.solution_code,
      clientId: "b3ea619b3f8a2a24845666b069b463f2",
      clientSecret:"eabbab96555556f8dd2c368f3b48225ffc3a28aab9a80de8f5ae05d979ba4809"
    }
    const output = await lastValueFrom(this.http.post('https://api.jdoodle.com/v1/execute',obj))
    this.solution_output = output
  }


}
