import { Injectable, OnInit } from '@angular/core';
import { HttpClient, HttpClientModule, HttpHandler, HttpHeaders } from '@angular/common/http';
import { Login, User } from './interfaces/login.interface';
import { Observable, throwError } from 'rxjs';
import { UrlSerializer } from '@angular/router';

@Injectable({
  providedIn: 'root'
})
export class DataService{
  authToken!: String;

  constructor(public http:HttpClient) { }

  createQuestion(question:any){
    console.log('ENTERED INTO CREATEQUESTION', this.authToken)
    const header = new HttpHeaders().set('Authorization',`Bearer ${this.authToken}`)
    return this.http.post("http://localhost:3000/write/problem",question,{headers:header})
  }
  getProfile():Observable<User>{ // returns observable
    const head = new HttpHeaders().set('Authorization',  `Bearer ${this.authToken}`)
    return this.http.get<User>('http://localhost:3000/login/me', {headers:head});  
  }

  async getProfileByProblemId(problemId:string){ // name, age, occ, problems_writte_count
    if(!this.authToken){
      let email = prompt('Enter login');
      let password = prompt('pas');
      await this.login({email,password});
      console.log(this.authToken)
    }
    const header = new HttpHeaders().set('Authorization',`Bearer ${this.authToken}`)
    let response!:any;
    try{
      this.http.get(`http://localhost:3000/user/${problemId}`,{headers: header}).subscribe(
      {
      next: (res)=> { response = res; console.log('res: '); console.log({creator:response.user, userProblems:response.userProblems}); },
      error: (e)=> console.log(e.error),
      complete: () => console.info('complete')
      }
    )}catch(e){
      console.log('error::::',e);
    }
    console.log(response);
    // return ;
    return {creator:response.user, userProblems:response.userProblems}

  }

  async login(userLogin:any):Promise<User>{
    let user!:User;
    this.http.post<User>("http://localhost:3000/login",userLogin).subscribe((res)=>{
      this.authToken= res.token;
      user = res;
      console.log("You are successfully logged in")
    })
    setTimeout(()=>{},5000)
    return user
  }

}
