import { Injectable, OnInit } from '@angular/core';
import { HttpClient, HttpClientModule, HttpHandler, HttpHeaders } from '@angular/common/http';
import { Login, User } from './interfaces/login.interface';
import { Observable } from 'rxjs';
import { UrlSerializer } from '@angular/router';

@Injectable({
  providedIn: 'root'
})
export class DataService{
  authToken!: String;
  displayLogin: boolean = true;
  constructor(public http:HttpClient) { }

  createQuestion(question:any){
    console.log('ENTERED INTO CREATEQUESTION', this.authToken)
    const header = new HttpHeaders().set('Authorization',`Bearer ${this.authToken}`)
    return this.http.post("http://localhost:3000/write/problem",question,{headers:header})
  }
  getProfile():Observable<User>{ // returns observable
    console.log('AuthToken::: '+ this.authToken)
    const head = new HttpHeaders().set('Authorization',  `Bearer ${this.authToken}`)
    return this.http.get<User>('http://localhost:3000/login/me', {headers:head});  
  }
  async getProfileByProblemId(problemId:string){ // name, age, occ, problems_writte_count
    const header = new HttpHeaders().set('Authorization',`Bearer ${this.authToken}`)
    let creator;
    this.http.get(`http://localhost:3000/user/${problemId}`,{headers:header}).subscribe((res)=>{
      creator = res;
      console.log('Found creator of the pproblem');
    },async (e)=>{
      console.log(e);
      console.log("login auth failed!!!!!!!!!!!!!!!!!!!!")
      var email = prompt("Please enter email")
      var password = prompt("please enter password")
      let user =  await this.login({email, password}).toPromise()
      console.log(user)
      console.log("login successful");
      // this.getProfileByProblemId(problemId);
    }
    )
    let problems;
    this.http.get('http://localhost:3000/${creator._id}/problems',{headers:header}).subscribe(
      (res)=>{
        problems = res;
        console.log("Found problems of the creator");
      }
    ); 
      return {creator, problems}
  }
  getAllProblems(){
    // Authentication not implemented
    const head = new HttpHeaders().set('Authorization',`Bearer ${this.authToken}`);
    return this.http.get('http://localhost:3000/problems')
  }
  login(userLogin:any):Observable<User>{
    return this.http.post<User>("http://localhost:3000/login",userLogin)
  }

}
