    let creator:any;
    let problems:any;
      this.http.get(`http://localhost:3000/user/${problemId}`,{headers:header}).subscribe((res)=>{
        creator = res;
        console.log('Found creator of the pproblem');
        })
        setTimeout(() => {

        }, 2000);
    this.http.get('http://localhost:3000/${creator._id}/problems',{headers:header}).subscribe(
      (res)=>{
        problems = res;
        console.log("Found problems of the creator");
      }
    ); 
    console.log(creator);
    console.log(problems)
    return {creator, problems}
  }
  getAllProblems(){
    // Authentication not implemented
    const head = new HttpHeaders().set('Authorization',`Bearer ${this.authToken}`);
    return this.http.get('http://localhost:3000/problems')
  }
  login(userLogin:any):Observable<User>{
    return this.http.post<User>("http://localhost:3000/login",userLogin)
  }

}
